var link=document.querySelector(".js-open-modal"),popup=document.querySelector(".modal-feedback"),close=popup.querySelector(".modal-feedback-close"),overlay=document.querySelector(".modal-overlay");link.addEventListener("click",function(a){a.preventDefault(),overlay.classList.add("modal-overlay-show"),popup.classList.add("modal-feedback-show")}),close.addEventListener("click",function(a){a.preventDefault(),popup.classList.remove("modal-feedback-show"),overlay.classList.remove("modal-overlay-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&popup.classList.contains("modal-feedback-show")&&popup.classList.remove("modal-feedback-show")});